-- Create "users" table
CREATE TABLE `users` (`uid` varchar(10) NOT NULL COMMENT "User Unique ID", `avatar_url` varchar(45) NOT NULL COMMENT "User Avatar", `username` varchar(25) NOT NULL COMMENT "User Name", `email` varchar(45) NOT NULL COMMENT "User Email", `email_verified` bit NULL DEFAULT b'0' COMMENT "User Email Verification Flag", `password` varchar(45) NOT NULL COMMENT "User Password", `nickname` varchar(25) NOT NULL COMMENT "User Nickname", `created_at` datetime NULL COMMENT "Created Time", `updated_at` datetime NULL COMMENT "Updated Time", `deleted_at` datetime NULL COMMENT "Soft deleted Time", `last_login` datetime NULL COMMENT "Last Login Time", `login_attempts` int NULL DEFAULT 0 COMMENT "User Login Attempts", `lock` bit NULL DEFAULT b'0' COMMENT "User Login Lock", `banned` bit NULL DEFAULT b'0' COMMENT "User Banned control", PRIMARY KEY (`uid`), UNIQUE INDEX `email` (`email`), INDEX `idx_email` (`email`), INDEX `idx_username` (`username`), UNIQUE INDEX `uid` (`uid`), UNIQUE INDEX `username` (`username`)) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci;
