-- Create "sentences" table
CREATE TABLE `sentences` (`user_uid` varchar(10) NOT NULL COMMENT "User Unique ID", `word_id` int unsigned NOT NULL COMMENT "Word ID", `content` varchar(200) NOT NULL COMMENT "Sentence content", `created_at` datetime NULL COMMENT "Created Time", `deleted_at` datetime NULL COMMENT "Soft deleted Time", PRIMARY KEY (`user_uid`, `word_id`), INDEX `word_id` (`word_id`), CONSTRAINT `sentences_ibfk_1` FOREIGN KEY (`user_uid`) REFERENCES `users` (`uid`) ON UPDATE RESTRICT ON DELETE CASCADE, CONSTRAINT `sentences_ibfk_2` FOREIGN KEY (`word_id`) REFERENCES `words` (`id`) ON UPDATE RESTRICT ON DELETE CASCADE) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci;
